-- 导出  表 jw.artcomment 结构
CREATE TABLE IF NOT EXISTS `artcomment` (
  `comid` int(11) NOT NULL AUTO_INCREMENT COMMENT '评论ID',
  `pid` int(5) NOT NULL DEFAULT '0' COMMENT 'parentID,如果是回复评论,回复的那条评论的ID',
  `artid` int(11) NOT NULL COMMENT '所属文章ID',
  `content` text NOT NULL,
  `addtime` datetime NOT NULL,
  `status` tinyint(1) NOT NULL DEFAULT '1',
  PRIMARY KEY (`comid`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8 COMMENT='文章评论表';

