-- 导出  表 jw.article 结构
CREATE TABLE IF NOT EXISTS `article` (
  `artid` int(11) NOT NULL AUTO_INCREMENT COMMENT '文章ID',
  `cateid` int(2) DEFAULT NULL COMMENT '所属栏目ID',
  `title` char(100) DEFAULT NULL COMMENT '标题',
  `content` text,
  `addtime` datetime DEFAULT NULL COMMENT '添加时间',
  `author` char(10) DEFAULT NULL COMMENT '作者',
  `likes` int(10) NOT NULL DEFAULT '0' COMMENT '被喜欢的次数',
  `views` int(10) NOT NULL DEFAULT '0' COMMENT '被浏览次数',
  `comments` int(10) DEFAULT '0' COMMENT '一级评论次数',
  `status` tinyint(1) DEFAULT '1',
  PRIMARY KEY (`artid`)
) ENGINE=InnoDB AUTO_INCREMENT=12 DEFAULT CHARSET=utf8 COMMENT='文章信息表';

-- 正在导出表  jw.article 的数据：~6 rows (大约)
/*!40000 ALTER TABLE `article` DISABLE KEYS */;
INSERT INTO `article` (`artid`, `cateid`, `title`, `content`, `addtime`, `author`, `likes`, `views`, `comments`, `status`) VALUES
	(1, 1, '2018年大前端发展趋势', '<p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">回顾2017年前端市场，可谓如火如荼。各种前端的框架/库层出不穷，给大家带来方便的同时，也使得前端开发人员面临更多的学习成本。</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">2018年的前端会有怎样的变化呢？</p><h1 style="margin: 20px auto; padding: 0px; font-weight: bolder; line-height: 1.2; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">一、微信小程序</h1><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"><img class="alignnone size-full wp-image-15558 aligncenter" src="http://static.jiawin.com/uploads/2017/03/19110948c4de1708303bdb6.jpg?imageView2/2/w/600/h/400" width="600" height="400"/></p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">微信小程序</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">微信小程序是一种可以媲美原生app的“app”，上市当天便在业内引起较大轰动。其实早在小程序之前也有百度轻应用这样的产品，很遗憾并未做起来。</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">究竟小程序会爆发多大的威力，我们拭目以待。但是可以肯定的是，一定会催生大量的小程序开发人员。而小程序用到的开发语言，就是我们前端的Javascript。相对于其他开发人员，这也是前端开发的一波红利。</p><h1 style="margin: 20px auto; padding: 0px; font-weight: bolder; line-height: 1.2; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">二、Vue.js</h1><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"><img class="size-full wp-image-15559 aligncenter" src="http://static.jiawin.com/uploads/2017/03/1911094955fc9a11564585e.jpg?imageView2/2/w/600/h/249" width="600" height="249"/></p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">Vue.js</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">是一个构建数据驱动的 web 界面的渐进式MVVM框架。Vue.js 的目标是通过尽可能简单的 API 实现响应的数据绑定和组合的视图组件。它只聚焦视图层，是一个构建数据驱动的Web界面的库。通过简单的API（应用程序编程接口）提供高效的数据绑定和灵活的组件系统。</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">它有如下优点：</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">1.轻量级的框架</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">2.双向数据绑定</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">3.指令</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">4.插件化</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">作为一个国产框架，又做的如此优秀，这里强烈推荐大家学习使用该框架。</p><h1 style="margin: 20px auto; padding: 0px; font-weight: bolder; line-height: 1.2; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">三、React</h1><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"><img class="alignnone size-full wp-image-15560 aligncenter" src="http://static.jiawin.com/uploads/2017/03/191109499a3bb0c6e4458c7.jpg?imageView2/2/w/550/h/241" width="550" height="241"/></p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">React</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">React 起源于 Facebook 的内部项目，因为该公司对市场上所有 JavaScript MVC 框架，都不满意，就决定自己写一套，用来架设 Instagram 的网站。React 的设计思想极其独特，属于革命性创新，性能出众，代码逻辑却非常简单。所以，越来越多的人开始关注和使用，认为它可能是将来 Web 开发的主流工具。</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">和Vue有点类似，React采用特殊的JSX语法，Vue.js在组件开发中也推崇编写.vue特殊文件格式，对文件内容都有一些约定，两者都需要编译后使用。</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">区别于Vue.js使用的是DOM模板，而React依赖Virtual DOM。React采用的Virtual DOM会对渲染出来的结果做脏检查。</p><h1 style="margin: 20px auto; padding: 0px; font-weight: bolder; line-height: 1.2; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">四、es6</h1><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"><img class="alignnone size-full wp-image-15561 aligncenter" src="http://static.jiawin.com/uploads/2017/03/19110950475c4bb845196c8.jpg?imageView2/2/w/600/h/450" width="600" height="450"/></p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">es6</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">ECMAScript 6.0（简称ES6）是JavaScript语言的下一代标准，已经在2015年6月正式发布了。它的目标，是使得JavaScript语言可以用来编写复杂的大型应用程序，成为企业级开发语言。相对于es5它有如下特点：</p><blockquote style="margin: 0px; padding: 0px 0px 0px 15px; list-style: none; color: rgb(153, 153, 153); border-left: 1px solid rgb(225, 225, 225); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"><ul style="list-style-type: none;" class=" list-paddingleft-2"><li><p>箭头函数</p></li><li><p>字符串</p></li><li><p>解构</p></li><li><p>模块</p></li><li><p>参数</p></li><li><p>类</p></li></ul></blockquote><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">等等重大变革……</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">你可以简单理解es6相对于es5，犹如css2.0相对于css3.0。虽然es6还不能做到所有浏览器都支持，但这或许也不影响你开始学习它。</p><h1 style="margin: 20px auto; padding: 0px; font-weight: bolder; line-height: 1.2; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">五、支付宝小程序</h1><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"><img class="alignnone size-full wp-image-15562 aligncenter" src="http://static.jiawin.com/uploads/2017/03/191109528d76bc79c2fc5d2.jpg?imageView2/2/w/470/h/276" width="470" height="276"/></p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">支付宝小程序</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">无独有偶，支付宝也将推出小程序（其实其团队内部已经好几个产品是基于支付宝小程序开发的了）。如果说微信小程序能够让前端翻了天，那我们也相信支付宝小程序也会是一股强大的力量。让我们持续关注吧。</p><h1 style="margin: 20px auto; padding: 0px; font-weight: bolder; line-height: 1.2; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">六、Electron</h1><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"><img class="alignnone size-full wp-image-15563 aligncenter" src="http://static.jiawin.com/uploads/2017/03/1911095224ddb57abe90a18.jpg?imageView2/2/w/600/h/284" width="600" height="284"/></p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">Electron</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">Electron 提供了一个实时构建桌面应用的纯 JavaScript 环境。简单来说，你可以使用Electron 来写一个桌面程序。比如：一个可以运行在windows上的百度云软件。</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">高兴的是，现在electron支持window 7和之上版本,osx 10.9之后,Linux等等，各个平台大部分几乎都做到了支持。</p><p><br/></p>', '2018-01-30 00:54:23', 'jsd', 1, 6, 0, 1),
	(3, 3, '项目优化的起承转合', '<p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">不知道大家有没有这样的经历：当自己负责的产品需求上线一段日子后，发现活跃用户逐渐减少，这时我们不禁疑惑，究竟怎样才能激发用户的持续关注？项目优化不失为好的方法之一。要做项目优化，这不单是运营同学的事情，在设计以及技术实现层面上，我们也应该思考如何给项目带来一个好的方案。每一次的优化探索，都是一段结果未知的旅程。这里举例QQ会员公众号过期提醒项目，和大家聊聊我们的小尝试。</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">起承转合这四个字并不陌生，在设计、音乐、文学等领域上经常会出现。在这里，我也用了“起承转合”来总结整个项目优化过程。</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"><img class="alignnone size-full wp-image-14810 aligncenter" src="http://static.jiawin.com/uploads/2016/12/07125948fadf92163a821cf.jpg?imageView2/2/w/550/h/517" alt="step" width="550" height="517"/></p><h2 style="margin: 20px auto; padding: 0px; font-weight: bolder; line-height: 1.2; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">一、起</h2><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">“起”，就是事情的起因。一开始，针对QQ会员/超级会员即将到期或者已经到期的用户，我们会通过公众号信息的形式提醒用户：你的QQ会员已到期。然而，为了提供干净的界面给用户，不带给用户过多的骚扰，今年5月份开始“QQ会员”手Q公众号被收进“服务号”。随之带来的是<strong>手Q公众号催费渠道付费数开始下降。</strong></p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">起初公众号推送模版为纯文字的固定模版，界面如下：</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"><img class="alignnone size-full wp-image-14811 aligncenter" src="http://static.jiawin.com/uploads/2016/12/071259491997e5b597926dd.jpg?imageView2/2/w/590/h/213" alt="overdue" width="590" height="213"/></p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">人都是没有耐心的，这种密密麻麻文字堆积的说明书形式的表现方式，加上本来的一级入口变成了二级入口，用户自然没有欲望点击。</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">交代了背景后，可以看出QQ会员公众号催费项目面临着几个问题：</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">1. “QQ会员”手Q公众号被收进服务号。</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">2. 目前的模板形式过于单调。</p><h2 style="margin: 20px auto; padding: 0px; font-weight: bolder; line-height: 1.2; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">二、承</h2><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">针对这些痛点，我们开始构思公众号过期提醒的改版。“承”这一块，主要是描述改版的探索过程。</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">在改版公众号展示方式的构思上，目前有几种方案可以选择：</p><table><tbody><tr class="firstRow"><td style="margin: 0px; list-style: none; border-color: rgb(229, 229, 229);">解决方式</td><td style="margin: 0px; list-style: none; border-color: rgb(229, 229, 229);">优点</td><td style="margin: 0px; list-style: none; border-color: rgb(229, 229, 229);">缺点</td></tr><tr><td style="margin: 0px; list-style: none; border-color: rgb(229, 229, 229);">纯文字模版</td><td style="margin: 0px; list-style: none; border-color: rgb(229, 229, 229);">配置简单</td><td style="margin: 0px; list-style: none; border-color: rgb(229, 229, 229);">说明文般乏味</td></tr><tr><td style="margin: 0px; list-style: none; border-color: rgb(229, 229, 229);">图文模版</td><td style="margin: 0px; list-style: none; border-color: rgb(229, 229, 229);">比纯文字模版内容丰富</td><td style="margin: 0px; list-style: none; border-color: rgb(229, 229, 229);">平庸单薄</td></tr><tr><td style="margin: 0px; list-style: none; border-color: rgb(229, 229, 229);">原生模版</td><td style="margin: 0px; list-style: none; border-color: rgb(229, 229, 229);">体验流畅</td><td style="margin: 0px; list-style: none; border-color: rgb(229, 229, 229);">开发成本大，需要跟版本发布</td></tr></tbody></table><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">显然目前的纯文字模版不足以满足我们的需求，或许用图文模版能带来好转，但两者都不具备灵活的布局和交互，模版比较死板。后来，了解到了在QQ运动、QQ天气的公众号上，他们用到动态模版的展示，动图如下（效果略卡顿与gif格式有关）：<br/><img class="alignnone size-full wp-image-14812 aligncenter" src="http://static.jiawin.com/uploads/2016/12/07125950ec07107acf7ce32.gif?imageView2/2/w/748/h/1332" alt="qqweather1" width="748" height="1332"/><br/>抛开技术实现方式来说，这种图＋文＋动画效果的表现方式，比起传统的公众号消息显然更有趣，并且更有画面感，让人一看就知道表达的信息是晴天还是下雨。于是产品经理在苦恼优化方案的时候，就想把这种展示形式移植到QQ会员公众号过期提醒上效果会怎样呢？尝试着用上这种动态模版，也许会使现状有所扭转。</p><h3 style="margin: 20px auto; padding: 0px; font-weight: bolder; line-height: 1.2; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);"><strong>构思：</strong></h3><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">通过了解，内部的即通开发团队自研的一套 Arkapp 框架，通过用&nbsp;Lua&nbsp;简洁、轻量、可拓展的脚本语言能实现这种动态模版。于是，我们迅速开始了策划－设计－开发－上线等一系列的流程。在开发过程中，也是踩过不少坑。</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">在整个过程中就像打怪一样，一步步击破，推敲出最优的效果。</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">设计师设计的时候，想在设计层面上跟结合会员品牌自身的属性，告诉用户：你要失去尊贵的会员身份了。同时，对于游戏用户在设计侧更有游戏氛围并且赠送游戏礼包来吸引用户。</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">最后，结合我们的需求以及想法，动画上会有一个企鹅＋文字从品牌色变灰的效果，同时底部会放多个礼包，支持点击滑动，在中心区域我们突出重点，显示过期后“80余项特权已失去”，以唤醒用户对QQ会员、超级会员这一身份的认知。</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">在两版设计稿中，通过推敲我们最后采用了第二版设计稿。</p><h3 style="margin: 20px auto; padding: 0px; font-weight: bolder; line-height: 1.2; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);"><img class="size-full wp-image-14813 aligncenter" src="http://static.jiawin.com/uploads/2016/12/07125951dff2b99d0150d28.jpg?imageView2/2/w/960/h/485" alt="demopage2" width="960" height="485"/><strong>开发：</strong></h3><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">在开发阶段，我们经历了一开始对框架完全不熟悉，得到框架开发团队的帮助以及看着框架 api 自己尝试，开始写效果，做动画。</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"><strong>动画方面</strong>，针对企鹅由品牌色变灰的效果以及文字变色和数字滚动的效果，我们尝试了流畅度不断优化以及不同实现方法的探索，得到最后相对让人舒服的效果。</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"><strong>性能优化方面</strong>，一开始我们把效果做出来的时候发现，效果是能够正常显示的，然而打开公众号窗口时，白屏时间太长，系统需要加载各种资源需要的时间太久了，于是我们通过探索经历了一系列的优化：元素加载时间优化，图片资源异步加载优化，减少包大小，达到减少页面白屏时间和增强动画流畅度……</p><h2 style="margin: 20px auto; padding: 0px; font-weight: bolder; line-height: 1.2; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">三、转</h2><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">如何判断改版优化是否有效？实践过后这个方法是可行还是不可行？“转”在诗文写作结构里是指事件结果的转折。我们要校验改版优化的结果，数据是一个很重要的体现。</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">在手Q会员公众号对到期会员游戏用户实现了动态模版＋游戏礼包引导催费，灰度效果喜人。</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">动态模版付费用户数是文字模版的5.4倍，催费消息点击率和付费转化率都比原本的文字模版有所提升。</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"><img class="alignnone size-full wp-image-14814 aligncenter" src="http://static.jiawin.com/uploads/2016/12/0712595157a13906ec8e75a.png?imageView2/2/w/575/h/319" alt="data" width="575" height="319"/></p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">当拿到产品经理给到的数据时，能看到对于这次的尝试虽然量小，但通过探索尝到了初步的甜头。通过对会员里的游戏用户，定制化地推送游戏动态模版，较于纯文本模版来说，在设计与动画展示上营造因为会员到期而特权福利失去的落差感，以及通过推送给用户的游戏礼包挽留，效果明显得到了优化。既然这个方法是有效提高转化率，能够吸引用户的，我们可以继续往深研究，覆盖到更广的面。</p><h2 style="margin: 20px auto; padding: 0px; font-weight: bolder; line-height: 1.2; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">四、合</h2><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">除了针对会员用户里的游戏用户推送带有游戏礼包福利的过期提醒动态模版外，整个公众号催费项目在其他应用场景上尝试用动态模版来实现推送模版优化，使得整个催费体系更加统一化。</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"><img class="alignnone size-full wp-image-14815 aligncenter" src="http://static.jiawin.com/uploads/2016/12/07125952060b3e4a340e14a.jpg?imageView2/2/w/650/h/550" alt="alldemo" width="650" height="550"/></p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">同时，因为在会员业务上已经初步收到了一些成效，在QQ钱包、游戏中心等其他业务上也开始寻求插入点并开始使用公众号动态模版。</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">在整个项目上我们已经初步收获了一些成果，在接下来的行动里，我们计划：</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">1. 沉淀归纳开发过程中遇到的问题，组件化代码使之更有复用性，积累经验提供给其他项目使用；</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">2. 不同的设计方案可能对转化率的影响也不一样，设计多一种方案进行A/B test 并且做好数据校验；</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">3. 这次项目我们抛开平时十分熟悉的web前端技术，尝试用 Lua 去做动画，可以把经验带到日常工作的其他项目中，预研包括但并不局限于web前端的其他技术，为项目带来更多更大的价值。</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">对于这次的QQ会员公众号过期提醒优化项目，我们通过借鉴优秀的案例，结合自身产品属性进行消化、合理利用并且真正落地校验，在不断的探索中优化。也许这次公众号优化的方法不是最优的，它还存在一些缺点，例如开发成本大，表现形式依然不够新颖等，但整个摸索尝试的过程，到最后得到喜人的数据，正是从0到1之后我们经过思考与探索得来的成果。</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">这个小小的产品优化案例，就是结合了起－承－转－合四大阶段去实现的，这样上下一脉相承，可以帮助我们了解整个方案实现的历程，发现更多的不足再持续优化。引用小马哥的一句话，像“小白”用户那样思考。每天高频使用产品，不断发现不足。未来我们在表现方式、交互形式上还需平时多关注业界动态，把最新的技术落实到业务上，创造不一样的价值。</p><p><br/></p>', '2018-01-30 01:00:22', 'jsd', 0, 0, 0, 1),
	(4, 2, '动感特效轻松get', '<p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">新手设计师相较于资深设计师，后者技能更丰富且全面，很多人想要进阶资深却苦于培养新技能，笔者在经手不少视频动效项目后，对于技能进阶颇有心得。通常技能进阶的方向有如下两种：</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>钻研型，</strong>工作需求和自身特长强结合，原特长在工作中日益强大，影响力不断提高；</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>发散型，</strong>工作需求和自身特长有较大偏差，但经过努力让自己掌握了新技能，极大增强了职场的适应力和综合能力。</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255);text-align: center"><img class="alignnone wp-image-15371" src="http://static.jiawin.com/uploads/2017/02/201553439fd772dd759f1f9.png?imageView2/2/w/891/h/1010" alt="" width="891" height="1010"/>&nbsp;</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">两者各有千秋，笔者则是在发散型的艰难道路上一路飞奔后，大量积累了视频剪辑和动效制作的经验，希望给走发散型道路的设计师助一臂之力。</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">&nbsp;<img class="alignnone size-full wp-image-15372" src="http://static.jiawin.com/uploads/2017/02/2015534470e2ef93266c9b0.png?imageView2/2/w/456/h/522" alt="" width="456" height="522"/></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">我们的团队从“画报”开始，一个偏Loft感觉的照片分享app，到主打音乐MV的“企鹅MV”，最后到玩转动效的短视频app——“闪咖”，一路以来不断要求设计师的技能变得更加立体：设计能力从平面层次，提升到能稳定输出动效的层次，并且能够辅导新人去快速入门。</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">&nbsp;</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><img class="alignnone wp-image-15373" src="http://static.jiawin.com/uploads/2017/02/20155344e3b8bd3cabc511a.png?imageView2/2/w/1177/h/375" alt="" width="1177" height="375"/></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">&nbsp;</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>产品的功能形态不断变化</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>&nbsp;</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><img class="alignnone wp-image-15374" src="http://static.jiawin.com/uploads/2017/02/20155345baea51b748e51e5.png?imageView2/2/w/1214/h/325" alt="" width="1214" height="325"/></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">&nbsp;</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>个人的能力值形态也不断立体化</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">&nbsp;</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">然而我会告诉你，刚来的时候我只是个会PS和画插画的愣头青吗？</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><img class="alignnone wp-image-15375" src="http://static.jiawin.com/uploads/2017/02/201553463d4ed2c7569cd35.png?imageView2/2/w/340/h/375" alt="" width="340" height="375"/></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">目前的闪咖是一款玩法新鲜的视频app，最初以表演为切入点，一直持续更新音乐，滤镜和动效等，为大家能更好的秀出自己而服务，尤其微信推出了10秒短视频功能后，引爆了一波刷爆朋友圈的短视频需求，所以，迫切要求闪咖的设计师能在动效设计上扛起重担。</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">那就是我们正在研究的<strong>手绘特效</strong>。</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><img class="alignnone wp-image-15376" src="http://static.jiawin.com/uploads/2017/02/201553460f6d4db204e396d.png?imageView2/2/w/386/h/386" alt="" width="386" height="386"/>&nbsp;</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">什么是手绘特效？我们简单看一些案例：</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">1.&nbsp;&nbsp;&nbsp;&nbsp;苹果AppleWatch第一代中已经出现了在表屏上手绘图案传情的“调戏功能”，第二代有更多的加强；目前iPhone7之间也完全支持了，还增加了多种表达方式；手机的大尺寸和平板的使用也使得手绘特效在未来有更多的空间。</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">&nbsp;&nbsp;<img class="alignnone wp-image-15377 aligncenter" src="http://static.jiawin.com/uploads/2017/02/201553492da9b11c256d2a0.png?imageView2/2/w/1249/h/1057" alt="" width="1249" height="1057"/></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>花样百出的iPhone短信涂鸦</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>&nbsp;</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><img class="alignnone size-full wp-image-15378 aligncenter" src="http://static.jiawin.com/uploads/2017/02/20155351391246dd70b7915.png?imageView2/2/w/500/h/310" alt="" width="500" height="310"/></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>AppleWatch</strong><strong>表屏涂鸦特效</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>&nbsp;</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">2.国内装扮功能较好的app诸如美拍，小咖秀，Snow等，其特效，滤镜，贴纸等功能都有先发优势，而手绘特效则鲜有触及，国内外几家大家熟悉的，如美图秀秀目前只支持在图片上涂鸦，Facebook尚在研究中无明显动作，Funimate等的视频涂鸦效果不佳。</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">&nbsp; &nbsp;<img class="alignnone size-full wp-image-15379 aligncenter" src="http://static.jiawin.com/uploads/2017/02/201553524e97e7b06856e81.png?imageView2/2/w/877/h/736" alt="" width="877" height="736"/></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>美图秀秀（左）和Facebook（右）</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>&nbsp;</strong><img class="alignnone size-full wp-image-15380" src="http://static.jiawin.com/uploads/2017/02/201553533267d773993f7ca.png?imageView2/2/w/877/h/736" alt="" width="877" height="736"/></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>Funimate</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>&nbsp;</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">3.轻特效的操作方法简单，玩法多样，写字涂画，双击、单击、滑屏等，未来还可以延伸出更多特别的视觉效果。</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><img class="alignnone size-full wp-image-15381" src="http://static.jiawin.com/uploads/2017/02/20155354de39f66e217dce9.png?imageView2/2/w/385/h/254" alt="" width="385" height="254"/>&nbsp;</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><img class="alignnone size-full wp-image-15382" src="http://static.jiawin.com/uploads/2017/02/201553558affcac9116167f.png?imageView2/2/w/475/h/304" alt="" width="475" height="304"/></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">&nbsp;</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">在确定了大方向后，应该如何快速构建这一功能呢？经过研究发现，大部分手绘特效，都是由“<strong>粒子特效</strong>”为核心展开设计的，只要能够懂“粒子”，一切就迎刃而解。粒子特效乍看之下很复杂，但我有简单轻松的构建方法，<strong>能让特效零基础的设计师快速建立起粒子特效的设计能力！</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">下面我要开始严肃的讲解了。</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><img class="alignnone wp-image-15383" src="http://static.jiawin.com/uploads/2017/02/20155355c8d224f020fed9b.png?imageView2/2/w/386/h/418" alt="" width="386" height="418"/>&nbsp;</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">设计师从0到1建立起粒子特效模块，至少需要哪些东西来支持呢？下面我以闪咖项目中，手绘特效功能的构建为案例，讲解建立它所需要的<strong>三大“齿轮”</strong>:</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><img class="alignnone wp-image-15384" src="http://static.jiawin.com/uploads/2017/02/20155356bcd1845e2d8a7ac.png?imageView2/2/w/1107/h/869" alt="" width="896" height="693"/></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>三大“齿轮”</strong></p><h2 style="margin: 20px auto;padding: 0px;font-weight: bolder;line-height: 1.2;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;white-space: normal;background-color: rgb(255, 255, 255)"><strong>齿轮一：提出设计方案，制作初级demo的能力——</strong></h2><h2 style="margin: 20px auto;padding: 0px;font-weight: bolder;line-height: 1.2;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;white-space: normal;background-color: rgb(255, 255, 255)"><strong>AE CC – particular粒子插件</strong></h2><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><img class="alignnone wp-image-15385" src="http://static.jiawin.com/uploads/2017/02/201553598c7c1e00b23414a.png?imageView2/2/w/1202/h/1045" alt="" width="944" height="822"/></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">AE可以制作大部分我们常见的特效，而“粒子特效”是里面一个相对来说比较高级的插件，我们可以在“效果”，“trapcode”里找到并打开“particular”进行制作，大部分网上下载的AE没有这个插件，需要单独去下载安装哦。</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><img class="alignnone wp-image-15386 aligncenter" src="http://static.jiawin.com/uploads/2017/02/20155401a8417a1584ae5e8.png?imageView2/2/w/1294/h/787" alt="" width="1294" height="787"/></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">&nbsp;</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">而使用这个粒子特效插件，可以做出的效果很多，我列举一个基本款：</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><img class="alignnone size-full wp-image-15387" src="http://static.jiawin.com/uploads/2017/02/2015540270a2f5c1cf1dce9.png?imageView2/2/w/284/h/307" alt="" width="284" height="307"/></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>流星</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><img class="alignnone size-full wp-image-15388" src="http://static.jiawin.com/uploads/2017/02/201554030e52ea18643d240.gif?imageView2/2/w/561/h/600" alt="" width="561" height="600"/></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>动态展示gif</strong>&nbsp;</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">其运动的轨迹也可自定义，具体的方法有兴趣研究可以私聊。不直接用AE和开发经行数据对接，是因为其参数过于庞大且复杂，每次提供相关参数就已经消耗了大部分精力和时间，所以对于设计师来说，仅用于制作特效demo的话就会轻松不少，但和开发对接参数可以另辟蹊跷，接下来就会讲到。</p><h1 style="margin: 20px auto;padding: 0px;font-weight: bolder;line-height: 1.2;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;white-space: normal;background-color: rgb(255, 255, 255)"></h1><h2 style="margin: 20px auto;padding: 0px;font-weight: bolder;line-height: 1.2;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;white-space: normal;background-color: rgb(255, 255, 255)"><strong>齿轮二：批量输出，并与开发对接数据的能力——</strong></h2><h2 style="margin: 20px auto;padding: 0px;font-weight: bolder;line-height: 1.2;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;white-space: normal;background-color: rgb(255, 255, 255)"><strong>particle design</strong></h2><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><img class="alignnone wp-image-15389" src="http://static.jiawin.com/uploads/2017/02/20155405734585f542e37ac.png?imageView2/2/w/1110/h/965" alt="" width="801" height="676"/><strong>&nbsp;</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong><img class="size-full wp-image-15390 aligncenter" src="http://static.jiawin.com/uploads/2017/02/20155406a8f5e889ccc28c4.jpg?imageView2/2/w/960/h/635" alt="" width="798" height="587"/></strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">官方介绍:粒子特效设计是一款功能强大的粒子特效编辑器,目前只支持mac上使用。使用者之间有云端共享功能，可以自定义粒子上传，自定义背景，和编辑粒子的各种变化的方法。官网下载地址：<a href="https://71squared.com/particledesigner" target="_blank" style="border: 0px;color: rgb(0, 195, 182);outline: none;transition: all 0.3s ease-out">https://71squared.com/particledesigner</a>&nbsp;（需要花钱购买正版哦）参考下图可以快速认识到该软件的基本形态。</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><img class="alignnone wp-image-15391" src="http://static.jiawin.com/uploads/2017/02/201554083bbe47ae3cde289.jpg?imageView2/2/w/1203/h/903" alt="" width="1093" height="795"/></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">用我的话来说就是一个可以弥补AE过于复杂的数据对接，能和开发轻松对接粒子特效的参数的软件，还能超级轻松的做出如下粒子效果：</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><img class="alignnone size-full wp-image-15392 aligncenter" src="http://static.jiawin.com/uploads/2017/02/20155409e0577bfa0399f40.png?imageView2/2/w/602/h/287" alt="" width="602" height="287"/></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>&nbsp; &nbsp;火花（左）和 &nbsp;下雨（右）</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">闪咖目前在做的特效有：</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><img class="alignnone size-full wp-image-15393 aligncenter" src="http://static.jiawin.com/uploads/2017/02/20155410a3e9d69042948c1.png?imageView2/2/w/871/h/271" alt="" width="871" height="271"/></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;雪花（左） &nbsp;星光涌现（中） &nbsp;泡泡飞扬（右） &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">&nbsp;</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">用它制作粒子效果非常便捷，只需要记住几点就可以融会贯通，我结合雪花飘散这个效果的制作过程来和大家解析：</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><img class="alignnone size-full wp-image-15394 aligncenter" src="http://static.jiawin.com/uploads/2017/02/2015541115ea64fa6d60d3e.gif?imageView2/2/w/750/h/580" alt="" width="750" height="580"/></p><h3 style="margin: 20px auto;padding: 0px;font-weight: bolder;line-height: 1.2;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;white-space: normal;background-color: rgb(255, 255, 255)"><strong>如何快速设计一个粒子效果</strong></h3><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>1.上云端共享寻找案例</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">设计一个粒子，无从下手的时候，建议你先点这里进入云端，浏览并观看其他用户已经做好并上传到共享的粒子效果，选择可借鉴的效果直接拿下来改进；自己设计好的也可以上传一份分享给他人参考。</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><img src="http://km.oa.com/files/photos/pictures/201702/1486977097_87_w295_h185.jpg?imageView2/2/w/960"/></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>点这里进入云端</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><img src="http://km.oa.com/files/photos/pictures/201702/1486977103_31_w286_h755.jpg?imageView2/2/w/960"/></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>这些都是使用者共享出来的粒子设计</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>2.自定义粒子的单位样式</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">要原创一个雪花飘散的效果，首先要通过PS设计一个基本单位，导出PNG格式，然后替换particle design里一个粒子特效的源图像，如图我在PS上设计了一个雪花放到某个粒子“源图像”里替换，替换了之后可以得到飘散效果的基本形态了。</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><img class="alignnone size-full wp-image-15395" src="http://static.jiawin.com/uploads/2017/02/201554126b27f71a6129716.png?imageView2/2/w/707/h/396" alt="" width="707" height="396"/>&nbsp; &nbsp;&nbsp;</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>左图为在源图像中放入做好的PNG，右图的基本单位从左边得来</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>3.调整粒子的动态变化参数</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">然后就是调整参数，因为调整参数时对应的效果会在屏幕预览中直接呈现，所以大家全部选项都玩一遍后，基本都能掌握，雪花飘散的关键参数大概如下截图标出的区域：</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><img src="http://km.oa.com/files/photos/pictures/201702/1486976746_52_w600_h943.jpg?imageView2/2/w/960"/></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><img src="http://km.oa.com/files/photos/pictures/201702/1486976733_76_w600_h869.jpg?imageView2/2/w/960"/></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">&nbsp;</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>4.粒子相互叠加</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">很多粒子特效都有特别华丽的效果，其本质上是多个粒子源叠加在一起散射的结果，其实就是把多个粒子放在一起，如我将粒子1,2叠加在一起就能得到混合的效果:</p><h2 style="margin: 20px auto;padding: 0px;font-weight: bolder;line-height: 1.2;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;white-space: normal;background-color: rgb(255, 255, 255)">&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;<img class="alignnone size-full wp-image-15396" src="http://static.jiawin.com/uploads/2017/02/20155412329262c080720d9.png?imageView2/2/w/730/h/177" alt="" width="730" height="177"/></h2><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>&nbsp;粒子1实心爱心 &nbsp;+ &nbsp;粒子2空心爱心&nbsp;= 结合的爱心</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">以上就是particle design输出能力的展示。这个小软件的遗憾在于不能单独输出demo视频或者PNG序列，仅能输出粒子项目文件，所以前期才需要AE的支持来先输出demo视频提案，接下来讲到的部分，既是解决该问题的方法，又是保持该软件能长期发力的关键。</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">&nbsp;</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">&nbsp;</p><h2 style="margin: 20px auto;padding: 0px;font-weight: bolder;line-height: 1.2;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;white-space: normal;background-color: rgb(255, 255, 255)"><strong>齿轮三：检验粒子效果，还原动效和导出视频的能力——</strong></h2><h2 style="margin: 20px auto;padding: 0px;font-weight: bolder;line-height: 1.2;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;white-space: normal;background-color: rgb(255, 255, 255)"><strong>“调试工具”</strong></h2><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><img class="alignnone wp-image-15397" src="http://static.jiawin.com/uploads/2017/02/20155414c05c824ef878793.png?imageView2/2/w/1052/h/914" alt="" width="726" height="658"/><strong>&nbsp;</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">从设计师的角度来说，检验分为两个步骤，上传自己设计好的粒子；体验它的实际效果，如下图<strong>(a传输粒子效果，b手机上调试)</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>a</strong><strong>传输粒子效果（左）&nbsp;b手机上调试 （右）</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">我一直在强调团队，因为构建这一完整的功能，开发团队也功不可没，particle design毕竟是单纯配合开发输出的工具，设计师无法直接在手机上进行效果检验，所以需要开发团队为设计师制作一个“<strong>调试工具</strong>”。接下来则是开发哥时间：</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>开发负责的流程大纲</strong></p><h3 style="margin: 20px auto;padding: 0px;font-weight: bolder;line-height: 1.2;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;white-space: normal;background-color: rgb(255, 255, 255)"><strong>制作“调试工具”：</strong></h3><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>1.调试客户端（即手机上一个可以检验特效的app）</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>2.服务器（用于存放设计提供的粒子文件）</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>3.上传工具（方便随时上传新设计的粒子文件，删除旧文件）</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">也就是说，开发的第一个任务是编写出以上三个功能，之后按照图中的步骤：<strong>从“网页工具”上传粒子到“服务器”，然后在“调试客户端”上下载“服务器”上存放的粒子文件进行效果检验，</strong>关于<strong>“调试客户端”</strong>如何接受粒子文件的参数来表现效果，简单来说有如下步骤：（以下是开发哥哥的原话）</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">1. 从视频中抽出视频帧，把每帧图像推入一个滤镜链</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">2. 滤镜链调用OpenGL接口处理图像，叠加光绘效果</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">3. 实现一套粒子系统解析引擎，计算光绘效果的粒子坐标</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">4. UI层响应用户手势，修正粒子发射器的位置</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">5. 滤镜链末端导出处理过的视频帧，调用视频编码接口写入视频文件</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">以上就是开发团队负责的部分，设计师可以据此思路推动自己的开发团队进行开发，需要更深层次代码交流的，欢迎各位私聊。接下来继续回到设计层面上。</p><h3 style="margin: 20px auto;padding: 0px;font-weight: bolder;line-height: 1.2;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;white-space: normal;background-color: rgb(255, 255, 255)"><strong>如何对粒子效果进行检验：</strong></h3><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>1.在线传输粒子文件到服务器</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">通过将particle design中导出的pex文件上传到服务器后，将粒子特效保存到服务器。</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>点击导出</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><br/></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>选择文件后，点击上传即可</strong></p><h2 style="margin: 20px auto;padding: 0px;font-weight: bolder;line-height: 1.2;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;white-space: normal;background-color: rgb(255, 255, 255)">&nbsp;</h2><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>2.手机端下载并测试，生成演示视频</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">打开手机上的调试工具客户端，在界面上点击一下“更新”，它会自动下载服务器上已上传的粒子，再点击“调试粒子1”可以切换到其他粒子继续看效果，直接在屏幕上涂画即可，镜头翻转自拍等，结合实际视频检验特效的效果。&nbsp;</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><img class="alignnone wp-image-15398" src="http://static.jiawin.com/uploads/2017/02/201554165f17db81672319e.jpg?imageView2/2/w/708/h/1259" alt="" width="578" height="1054"/></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">点击下方绿色button可以即时录制</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><img class="alignnone size-full wp-image-15399 aligncenter" src="http://static.jiawin.com/uploads/2017/02/20155418f48114f66aa46c0.gif?imageView2/2/w/400/h/711" alt="" width="400" height="711"/><br/><strong>这是个星星效果的demo</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">三个部件都齐全的你，就拥有了提出粒子特效设计方案，配合开发输出，并能自行调试还原效果的能力；接下来要讨论的是从1到100的这条路上，这个模块还有哪些地方需要完善呢？我个人总结了如下几点：</p><h3 style="margin: 20px auto;padding: 0px;font-weight: bolder;line-height: 1.2;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;white-space: normal;background-color: rgb(255, 255, 255)"><strong>完善调试工具</strong></h3><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>1.“调试工具客户端”增加导入视频的能力</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">在调试工具客户端中，加入可以导入本地视频的能力，这样一来，在调试工具客户端中检验效果时，直接导入一个合适的视频，两者结合即可输出一个完整的视频特效demo，大大提高了做出设计方案的可行性，减少了制作视频demo的时间，方便结合使用场景去检验制作的粒子特效是否合适，一举三得。</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>2.“调试工具客户端”支持多重粒子的能力</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">在调试工具客户端中，目前我们仅支持一种粒子特效展示效果，而我们前文提到，多重粒子叠在一起可以变得更加华丽，所以在调试工具客户端的模拟展示中，如果能支持结合2个以上粒子的检验方式，那么粒子特效的制作也有更多发挥空间，能做出更多更牛逼的效果。</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>3.particle design深层次的玩法挖掘</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">目前提到过的自定义粒子源和多重粒子叠加都是particle design的核心用法，那么他的价值还有哪些？这就与他软件本身的一些参数有关，现阶段能够解读到的参数作用在本文的软件介绍部分已经提过，而在云共享中仍能看到不少老外大神搞出的五花八门的玩法，这些都需要设计师自己来花时间研究。</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><img class="alignnone size-full wp-image-15400" src="http://static.jiawin.com/uploads/2017/02/20155418d2b9af41e745edb.gif?imageView2/2/w/750/h/580" alt="" width="480" height="483"/></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>&nbsp; &nbsp; &nbsp;撒钱币 &nbsp; &nbsp; &nbsp;</strong>&nbsp;&nbsp;</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><img class="alignnone size-full wp-image-15401" src="http://static.jiawin.com/uploads/2017/02/20155419bf3d5084a14dc99.gif?imageView2/2/w/750/h/580" alt="" width="481" height="390"/></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><strong>环环相扣</strong></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">到这里，整个轻松获得动效能力的方法已经介绍完了，有此神技，只要和团队一起合作就能构建出粒子特效（当然你自己会写程序，那你可以一夫当关！），并且优化完善后还可以普及给其他的团队成员，让粒子特效不再显得神秘和复杂。</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">设计师自身则能从曾经的平面设计师升级一个档次，获得技能提升的同时，在团队中也会收获<a style="border: 0px;color: rgb(0, 195, 182);outline: none;cursor: pointer;transition: all 0.3s ease-out"></a>较大的影响力，提升自己在项目运作中不可取代的价值。</p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)"><img class="alignnone wp-image-15402" src="http://static.jiawin.com/uploads/2017/02/2015542048125272b2f82e7.png?imageView2/2/w/646/h/611" alt="" width="646" height="611"/></p><p style="margin: 20px auto;padding: 0px;list-style: none;color: rgb(51, 51, 51);font-family: &#39;Hiragino Sans GB&#39;, &#39;Helvetica neue&#39;, Helvetica, &#39;Microsoft Yahei&#39;, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)">如果你的产品也在研究手绘笔触类的特效，不妨以此为开始走出第一步，在未来获得更好的方法后，分享给更多的人，感谢您的阅读。</p><p><br/></p>', '2018-01-30 01:09:35', 'jsd', 0, 0, 0, 1),
	(5, 1, '玩转H5下拉上滑动效', '<p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">在APP中我们经常会在页面下拉时看到一些动效，例如下拉图片跟随放大、下拉刷新出现loading动画等，这个交互细节在各个主流APP以及手机原生系统中随处可见，能为APP增添个性亮点。</p><h3 style="margin: 20px auto; padding: 0px; font-weight: bolder; line-height: 1.2; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">一、背景</h3><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">在我们的日常项目中，也会遇到这样的需求，在当前环境下，此类效果大多是靠原生来实现的，但是用原生实现有不少缺陷，原因如下：</p><ul style="list-style-type: none;" class=" list-paddingleft-2"><li><p>原生实现周期长;</p></li><li><p>跨平台实现成本高;</p></li><li><p>解决方案通用性差;</p></li><li><p>运用到现有项目改造成本高;</p></li></ul><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">那么我们的H5页面能否实现呢？下面和大家一起来探索。</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">首先，我们先来实现下拉banner图放大的动效。效果类似于：</p><p><img src="http://127.0.0.1/share/article/upload/image/20180130/1517247051__24201730b16cebfc400abf2-768x658.png" title="1517247051__24201730b16cebfc400abf2-768x658.png" alt="24201730b16cebfc400abf2-768x658.png"/></p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">动效要求：当处于顶部页面下拉时，banner图跟随放大，下拉结束时，banner图还原回弹。</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">下拉动效可以分解为两部分：</p><ol style="list-style-type: none;" class=" list-paddingleft-2"><li><p>页面主体内容整体下滑滚动;</p></li><li><p>顶部元素进行放大动效。</p></li></ol><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">在以往，在H5实现页面滚动效果是一般采用JS插件模拟滚动，例如：iScroll。但在这里存在几个问题：</p><ul style="list-style-type: none;" class=" list-paddingleft-2"><li><p>对现有页面的改造非常大，不适宜大范围运用;</p></li><li><p>模拟滚动取代原生滚动会引发未知的bug;</p></li><li><p>在低端手机性能差，体验不佳;</p></li></ul><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">要解决这些问题，可以采用如下方案：</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="color: rgb(255, 102, 0);">1，保持页面的原生滚动，只有在顶部下拉事件中，才触发动效执行;</span><br/><span style="color: rgb(255, 102, 0);">2，用JS拷贝DOM结构，使动效结构与页面结构解耦，互不影响;</span></p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">这样下拉动效会相对更流畅并基本不影响页面正常滚动。</p><h3 style="margin: 20px auto; padding: 0px; font-weight: bolder; line-height: 1.2; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">二、技术实现</h3><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">按照上面的技术方案实施，具体过程为：</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">禁用页面顶部下拉事件 &nbsp;——&gt; &nbsp;将页面的主体内容用一个DIV容器包含起来，同时复制需要放大处理的内容节点至主体内容之外 &nbsp;——&gt; 绑定页面滑屏事件 &nbsp;——&gt; 计算滑屏偏移量以及缩放等数值的运算 &nbsp;——&gt; 根据变化数值主体内容transfrom下滑，同时复制的节点做放大动效处理 &nbsp;——&gt; 监听滑屏事件结束，执行回弹动效，还原初始状态</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">代码节选片段：</p><p><img src="http://127.0.0.1/share/article/upload/image/20180130/1517247104__242017340c5b77393f6f851.png" title="1517247104__242017340c5b77393f6f851.png" alt="242017340c5b77393f6f851.png"/></p><p><br/></p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="color: rgb(255, 102, 0);"><strong>要点及说明：</strong></span></p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"><strong>1、禁用页面顶部下拉事件：</strong></p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">该技术要点是阻止默认事件的下拉，从而将下拉事件绑定到我们的动效中。阻止默认事件我们一般可以这样子处理：</p><p><img src="http://127.0.0.1/share/article/upload/image/20180130/1517247165__242017345fdce88e4d3e4cc.png" title="1517247165__242017345fdce88e4d3e4cc.png" alt="242017345fdce88e4d3e4cc.png"/></p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">但是此方法有一定缺陷。首先他禁止了页面的正常滑动，给页面的造成了不少的干扰和麻烦；其次是在一些APP中的WebView中并不能根本上的阻止View的下拉，对页面动效造成干扰。</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">这里最好的一个处理方法是可以在WebView的层面上去禁止页面下拉。</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"><strong>2、需判断滑屏方向，避免横向滑动干扰下拉动效。公式计算：</strong></p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">X = 绝对值（当前坐标X – 初始坐标X）<br/>Y = 绝对值（当前坐标Y – 初始坐标Y）</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">计算结果X大于Y，则为横向滑动，反之为竖向滑动。</p><p><img src="http://127.0.0.1/share/article/upload/image/20180130/1517247213__2420173352bb3363db39392.png" title="1517247213__2420173352bb3363db39392.png" alt="2420173352bb3363db39392.png"/></p><p><strong style="color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">3、滑屏偏移量，缩放动效及下拉动效的数值公式计算</strong></p><p><br/></p>', '2018-01-30 01:36:01', 'jsd', 0, 4, 0, 1),
	(6, 1, '玩转H5下拉上滑动效', '<p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">在APP中我们经常会在页面下拉时看到一些动效，例如下拉图片跟随放大、下拉刷新出现loading动画等，这个交互细节在各个主流APP以及手机原生系统中随处可见，能为APP增添个性亮点。</p><h3 style="margin: 20px auto; padding: 0px; font-weight: bolder; line-height: 1.2; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">一、背景</h3><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">在我们的日常项目中，也会遇到这样的需求，在当前环境下，此类效果大多是靠原生来实现的，但是用原生实现有不少缺陷，原因如下：</p><ul style="list-style-type: none;" class=" list-paddingleft-2"><li><p>原生实现周期长;</p></li><li><p>跨平台实现成本高;</p></li><li><p>解决方案通用性差;</p></li><li><p>运用到现有项目改造成本高;</p></li></ul><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">那么我们的H5页面能否实现呢？下面和大家一起来探索。</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">首先，我们先来实现下拉banner图放大的动效。效果类似于：</p><p><img src="http://127.0.0.1/share/article/upload/image/20180130/1517247051__24201730b16cebfc400abf2-768x658.png" title="1517247051__24201730b16cebfc400abf2-768x658.png" alt="24201730b16cebfc400abf2-768x658.png"/></p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">动效要求：当处于顶部页面下拉时，banner图跟随放大，下拉结束时，banner图还原回弹。</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">下拉动效可以分解为两部分：</p><ol style="list-style-type: none;" class=" list-paddingleft-2"><li><p>页面主体内容整体下滑滚动;</p></li><li><p>顶部元素进行放大动效。</p></li></ol><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">在以往，在H5实现页面滚动效果是一般采用JS插件模拟滚动，例如：iScroll。但在这里存在几个问题：</p><ul style="list-style-type: none;" class=" list-paddingleft-2"><li><p>对现有页面的改造非常大，不适宜大范围运用;</p></li><li><p>模拟滚动取代原生滚动会引发未知的bug;</p></li><li><p>在低端手机性能差，体验不佳;</p></li></ul><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">要解决这些问题，可以采用如下方案：</p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="color: rgb(255, 102, 0);">1，保持页面的原生滚动，只有在顶部下拉事件中，才触发动效执行;</span><br/><span style="color: rgb(255, 102, 0);">2，用JS拷贝DOM结构，使动效结构与页面结构解耦，互不影响;</span></p><p style="margin: 20px auto; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Hiragino Sans GB&quot;, &quot;Helvetica neue&quot;, Helvetica, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);">这样下拉动效会相对更流畅并基本不影响页面正常滚动。</p><p><br/></p>', '2018-01-30 09:04:41', 'jsd', 0, 0, 0, 1),
	(10, 1, '34534', '<p><img src="http://jsd.web/share/article/upload/image/20180130/1517274733__242017340c5b77393f6f851.png" title="1517274733__242017340c5b77393f6f851.png" alt="242017340c5b77393f6f851.png"/></p>', '2018-01-30 09:12:16', 'jsd', 0, 0, 0, 1),
	(11, 1, '16116616', '<p><img src="http://jiangsd/share/article/upload/image/20180130/1517276319__24201730b16cebfc400abf2-768x658.png" title="1517276319__24201730b16cebfc400abf2-768x658.png" alt="24201730b16cebfc400abf2-768x658.png"/></p>', '2018-01-30 09:38:41', 'jsd', 0, 0, 0, 1);
